import React, { useState, useEffect, useContext } from 'react'

import Container from 'react-bootstrap/Container'
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'

import NavBarItems from './NavBarItems'

//import { AuthContext } from '../context/AuthProvider'

import logo from '../images/logo.png'
//import './NavigationBar.css'

const updateNavbarColor = () => {
	if (document.documentElement.scrollTop > 399 || document.body.scrollTop > 399) {
		setNavbarColor('navbar-color')
	} else if (document.documentElement.scrollTop < 400 || document.body.scrollTop < 400) {
		setNavbarColor('navbar-transparent')
	}
}

function NavigationBar() {
	const [ navbarColor, setNavbarColor ] = React.useState('navbar-transparent')

	const currentUser = null //useContext(AuthContext)

	useEffect(() => {
		window.addEventListener('scroll', updateNavbarColor)
		return function cleanup() {
			window.removeEventListener('scroll', updateNavbarColor)
		}
	}, [])

	return (
		<React.Fragment>
			<Navbar collapseOnSelect expand="lg" className={navbarColor} fixed="top">
				<Container>
					<Navbar.Brand href="/">
						<img className="main-logo" src={logo} />
					</Navbar.Brand>

					<Navbar.Toggle aria-controls="responsive-navbar-nav" />
					<Navbar.Collapse id="responsive-navbar-nav">
						<Nav className="ml-auto">
							<NavBarItems currentUser={currentUser} />
						</Nav>
					</Navbar.Collapse>
				</Container>
			</Navbar>
			<style jsx>{`
			.nav-link {
	text-decoration: none;
	margin: 1rem 1rem;
	text-transform: uppercase;
	font-size: 16px;
	letter-spacing: 2px;
	color: white !important;
}

.main-logo {
	width: 50px;
}

.navbar-transparent{
	background: transparent;
	transition: all 300ms ease;
}

.navbar-color{
	background: linear-gradient(to right, rgba(44, 83, 100, 1), rgba(32, 58, 67, 1), rgba(15, 32, 39, 1));
	transition: all 300ms ease;
}

.navbar-color .main-logo{
	width: 40px;
	transition: all 300ms ease;
}

.navbar-color .nav-link {
	text-decoration: none;
	margin: 0rem 1rem;
	text-transform: uppercase;
	font-size: 14px;
	letter-spacing: 2px;
	transition: all 300ms ease;
}

.navbar .form-control{
	width: 80% !important;
	background-color: transparent;
}

.navbar .form-control:focus{
	background-color: black;
}
			`}

			</style>
		</React.Fragment>
	)
}

export default NavigationBar
